import React, { useEffect, useState } from 'react'
import AdminNavbar from '../components/AdminNavbar'
import axios from 'axios';
import { Button, Container, Table, Row, Dropdown } from 'react-bootstrap';
import Swal from 'sweetalert2';

function AdminHome() {
    const [users, setUsers] = useState([]);
    const [change, setChange] = useState(false);
    const [modalShow, setModalShow] = React.useState(false);


    useEffect(async () => {
        const response = await axios.get("/admin/");
        setUsers(response.data);

    }, [change])

    async function blockUser(id) {
        try {
            await axios.post("/admin/block-user", { id });
            setChange(!change);
        } catch (e) {

        }
    }

    async function editUser(id) {
        try{
            await axios.post('/admin/edit-user', {id});

        } catch (e) {

        }
    }

    async function deleteUser(id) {
        const result = await Swal.fire({
            title: 'Are you sure?',
            text: 'User will have Admin Privileges',
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Yes!'
        })
        if (result.value) {
            await axios.post("/admin/delete-user", { id });
            setChange(!change);
        }
    }

    return (
        <div>
            <AdminNavbar />
            <Container className='mt-5'>
                <Row>
                    <div className='mb-3' style={{display:'flex', justifyContent:'flex-end'}}>
                        <input style={{ border:'1px solid #000', borderRadius:'5px', padding:'5px', width:'180px', marginRight:'5px'}} placeholder='Search User' />
                        <Button variant="outline-dark">Search</Button>
                    </div>
                    <Table striped bordered hover>
                        <thead>
                            <tr>
                                <th>#</th>
                                <th>Email</th>
                                <th>Password</th>
                                <th>Status</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {users.map((user) => {
                                return (<tr>
                                    <td>1</td>
                                    <td>{user.email}</td>
                                    <td>********</td>
                                    {user.isActive === true ? <td>Active</td> : <td style={{ color: 'red' }}>Blocked</td>}
                                    <td>
                                        <Dropdown>
                                            <Dropdown.Toggle variant="secondary" id="dropdown-basic">
                                                More
                                            </Dropdown.Toggle>

                                            <Dropdown.Menu>
                                                <Dropdown.Item onClick={() => blockUser(user._id)}>{user.isActive === true ? "Block" : "Unblock"}</Dropdown.Item>
                                                <Dropdown.Item onClick={() => setModalShow(true)}>Edit</Dropdown.Item>
                                                <Dropdown.Item onClick={() => deleteUser(user._id)}>Delete</Dropdown.Item>
                                            </Dropdown.Menu>
                                        </Dropdown>
                                    </td>
                                </tr>)
                            })}

                        </tbody>
                    </Table>
                </Row>
            </Container>
        </div>
    )
}

export default AdminHome
