import React, { useState } from 'react'
import { Button, Card, Col, Container, Form, Row } from 'react-bootstrap';
import { Link, useNavigate } from 'react-router-dom';

function AdminLogin() {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const navigate = useNavigate();

    async function login(e) {
        e.preventDefault();
        try {
            const loginData = {
                email,
                password
            };

            await axios.post("http://localhost:2000/admin/login", loginData);
            navigate("/admin");
        } catch (e) {
            console.error(e);
        }
    }


    return (
        <div>
            <Container style={{ height: '100vh' }}>
                <Row style={{ justifyContent: 'center', height: '100vh', alignItems: 'center' }}>
                    <Col xs={6}>
                        <Card style={{ justifyContent: 'center' }}>
                            <Card.Body >
                                <Card.Title className='py-3' style={{ textAlign: 'center' }}>Login</Card.Title>
                                <Form onSubmit={login}>
                                    <Form.Group className="mb-3" controlId="formBasicEmail">
                                        <Form.Label>Email address</Form.Label>
                                        <Form.Control onChange={(e) => setEmail(e.target.value)} type="email" placeholder="Email" />
                                    </Form.Group>

                                    <Form.Group className="mb-3" controlId="formBasicPassword">
                                        <Form.Label>Password</Form.Label>
                                        <Form.Control onChange={(e) => setPassword(e.target.value)} type="password" placeholder="Password" />
                                    </Form.Group>
                                    <Button style={{ width: '100%' }} variant="primary" type="submit">
                                        Login
                                    </Button>

                                </Form>
                                <div className='py-3' style={{ textAlign: 'center' }}>
                                    <Link to="/signup">Create Account</Link>
                                </div>

                            </Card.Body>
                        </Card>

                    </Col>
                </Row>
            </Container>
        </div>
    )
}

export default AdminLogin
